import React from 'react'

const Formula = ({ persons, newName, newNumber }) => {

    const handleNameChange = (event) => {
        console.log(event.target.value)
        setState({ newName: event.target.value})
      }
    
    const handleNumberChange = (event) => {
        console.log(event.target.value)
        setState({ newNumber: event.target.value})
      }


    const addName = (event) => {
        event.preventDefault()
        const nameCheck = newName
        const findRes = persons.find(function(pers){
          if(pers.name === nameCheck){
            return true
          } else {
            return false
          }
        })
        if(!findRes) {
          const persoName = { name: newName, number: newNumber}
          const names = persons.concat(persoName)
      
            persons.setState({
            persons: names,
            newName: '',
            newNumber: ''
          })
        } else {
            persons.setState({
            newName: '',
            newNumber: ''
          })
          alert('duplicate content!')
        }
      }

  return (
    <form onSubmit={addName}>
        <div>
            nimi: <input value={newName} onChange={handleNameChange}/>
        </div>
        <div>
            numero: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
            <button type="submit">lisää</button>
        </div>
    </form>
  )
}

export default Formula
